switch (item.ReasonCode)
{
    case "01" : return "POD : Serah kepada Penerima";
    case "02" : return "Serahan Gagal";
    case "03" : return "Serahan Gagal";
    case "04" : return "Serahan Gagal";
    case "05" : return "Serahan Gagal";
    case "06" : return "Serahan Gagal";
    case "07" : return "Serahan Gagal";
    case "08" : return "Serahan Gagal";
    case "09" : return "Serahan Gagal";
    case "10" : return "Serah kepada Pengirim";
    case "11" : return "POD : Serahan Tingkap";
    case "28" : return "Serahan Gagal";
    case "99" : return "Serahan Gagal";
    case "UNKNOWN" : return "Serahan";
    case "-" : return "Serahan";
}
return display;

var display = "-";
var comment = !string.IsNullOrEmpty(item.Comment) ? item.Comment : "-";
switch (item.ReasonCode)
{
    case "01" : 
      if (item.DropCode.Equals("N") && !item.RecipientLocation.Equals("06"))
      {
        return string.Format("Pod to Receiver: {0}, {1}", item.RecipientName, item.RecipientIc);
      }      
      return string.Format("Maildrop, {0}", item.LokasiDrop);
    case "02" : return string.Format("Tutup, {0}", comment);
    case "03" : return string.Format("Salah Alamat, {0}", comment);
    case "04" : 
        var status = "${status}";
        if (item.ReasonCode.Equals("04") && 
            (item.DamageCode.Equals("01") || 
            !string.IsNullOrEmpty(item.RecipientName) || 
            !string.IsNullOrEmpty(item.RecipientIc)|| 
            !string.IsNullOrEmpty(item.RecipientLocation)))
        {
            status = "Terima";
        }
        return string.Format("Rosak {0}, {1}", status, comment);
    case "05" : return string.Format("Enggan Terima, {0}", comment);
    case "06" : return string.Format("Future, {0}", comment);
    case "07" : return string.Format("Kegagalan Operasi, {0}", comment);
    case "08" : return string.Format("Enggan Beri Pengenalan, {0}", comment);
    case "09" : return string.Format("Tiada Penerima, {0}", comment);
    case "10" : return string.Format("Return To Sender: {0}, {1}", item.RecipientName, item.RecipientIc);
    case "11" : return string.Format("POD-Serahan Tingkap: {0}, {1}", item.RecipientName, item.RecipientIc);
    case "28" : return string.Format("Pindah, {0}", comment);
    case "99" : return string.Format("Lain-Lain, {0}", comment);
    case "UNKNOWN" : return string.Format("Kod: {2}, Nama &amp; IC Penerima (jika ada): {0}, {1}", item.RecipientName, item.RecipientIc, item.ReasonCode);
    case "-" : return string.Format("Nama &amp; IC Penerima (jika ada): {0}, {1}", item.RecipientName, item.RecipientIc);
}
return display;

decimal amount = 0;
if (item.TotalPayment.HasValue) amount = item.TotalPayment.Value;

if (!string.IsNullOrEmpty(item.CodAccount) && item.CodAccount != "-")
{
    return string.Format("AccountNo: {0}  | Amount:RM {1:0.00}", item.CodAccount, amount);
}
return null;

var display = "Kod Tidak Diketahui";
switch (item.ReasonCode)
