    case "10" : return "Pod";
    case "11" : return "Pod";
    case "28" : return "Pindah";
    case "99" : return "Lain-Lain";
}
return display;

double payment = 0;
var ok = double.TryParse(item.TotalPayment.ToString(), out payment);
return payment;

byte result = 0;
if (item.ReasonCode.Equals("04"))
{
    if (item.DamageCode.Equals("01") 
    || !string.IsNullOrEmpty(item.RecipientName) 
    || !string.IsNullOrEmpty(item.RecipientIc)
